export default function logCompileOutput(options, error, stats) {
    if (error) {
        console.error(error);
    }
    if (stats) {
        console.log(stats.toString({
            colors: true,
            version: false,
            hash: false,
        }));
    }
    if (options.verbose) {
        console.error("\nCompilation finished\n");
    }
}
